return validation;

// Enhanced Commission Calculator with Market Validation Data
// Use the actual payout percentage from the selected market
const validationData = $json;

if (!validationData.success) {
  throw new Error('Validation failed - cannot calculate commissions');
}

const stake = validationData.deltaAmount;
const selectedMarket = validationData.selectedMarket;
const actualPayoutPercentage = selectedMarket.averagePayoutPercentage;

console.log('üìä Using actual market payout percentage:', actualPayoutPercentage);
console.log('üéØ Selected market:', selectedMarket.displayName);
console.log('üí∞ Stake amount:', stake);

// Use the real payout percentage from Deriv instead of calculated commission
// This ensures we work with actual market conditions
function calculateWithRealPayout(stake, payoutPercentage) {
  const T = Number(stake);
  const realPayoutPercent = Number(payoutPercentage);

  // Validation
  if (!(T > 0)) return { ok: false, reason: "Please input a valid stake" };
  if (realPayoutPercent < 0) return { ok: false, reason: "Payout % cannot be negative" };

  // Calculate based on real Deriv payout
  const expectedPayout = T * (1 + realPayoutPercent / 100);
  const potentialProfit = expectedPayout - T;

  // For display purposes, we'll show the real market data
  return {
    ok: true,
    originalStake: Math.round(T * 100) / 100,
    realPayoutPercentage: Math.round(realPayoutPercent * 100) / 100,
    expectedPayout: Math.round(expectedPayout * 100) / 100,
    potentialProfit: Math.round(potentialProfit * 100) / 100,
    // Legacy fields for compatibility
    profitPercentage: Math.round(realPayoutPercent * 100) / 100,
    sentToExchange: Math.round(T * 100) / 100, // Full amount goes to exchange
    traderPayout: Math.round(expectedPayout * 100) / 100,
    actualProfitPercentage: Math.round(realPayoutPercent * 100) / 100
  };
}

const result = calculateWithRealPayout(stake, actualPayoutPercentage);

if (!result.ok) {
  throw new Error(result.reason);
}

return {
  ...result,
  // Enhanced data from validation
  deltaAmount: validationData.deltaAmount,
  tradeSymbol: validationData.tradeSymbol,
  duration: validationData.duration,
  position: validationData.position,
  timestamp: new Date().toISOString(),
  tradeId: `TRADE_${Date.now()}`,

  // Market selection data
  selectedMarket: validationData.selectedMarket,
  marketAnalysis: validationData.marketAnalysis,
  validationMessage: validationData.validationMessage,

  // Payout details for both positions
  payoutDetails: {
    rise: selectedMarket.risePayoutPercentage,
    fall: selectedMarket.fallPayoutPercentage,
    average: selectedMarket.averagePayoutPercentage,
    selectionReason: selectedMarket.selectionReason
  }
};

