// Store trade execution data for monitoring
const tradeResponse = $json;

if (!tradeResponse || !tradeResponse.success || !tradeResponse.data) {
  throw new Error("Invalid trade response from Deriv API");
}

const tradeData = tradeResponse.data;
const contractId = tradeData.contractId;

// Get original parameters from input
const inputData = $input.all()[0].json;
const originalStake = inputData.sentToExchange || inputData.deltaAmount || 1;
const tradeSymbol = inputData.tradeSymbol || "R_100";
const duration = inputData.duration || 5;
const position = inputData.position || "CALL";

console.log(`‚úÖ Trade executed successfully - Contract ID: ${contractId}`);
console.log(`‚è≥ Starting trade completion monitoring...`);

// Calculate expected expiration time with proper timing
const purchaseTime = tradeData.purchaseTime || Math.floor(Date.now() / 1000);
// For tick-based contracts, each tick is approximately 1 second
const expectedExpiryTime = (purchaseTime * 1000) + (duration * 1000) + 3000; // Add 3 second buffer
const currentTime = Date.now();
const calculatedWaitTime = expectedExpiryTime - currentTime;
const waitTime = Math.max(5000, calculatedWaitTime); // Minimum 5 seconds wait

console.log(`Purchase time: ${new Date(purchaseTime * 1000).toISOString()}`);
console.log(`Expected expiry: ${new Date(expectedExpiryTime).toISOString()}`);
console.log(`Current time: ${new Date(currentTime).toISOString()}`);
console.log(`Calculated wait: ${calculatedWaitTime}ms`);
console.log(`Final wait time: ${waitTime}ms (${Math.ceil(waitTime / 1000)} seconds)`);

// Return trade execution data with monitoring info
return {
  // Trade identification
  tradeId: `TRADE_${contractId}_${Date.now()}`,
  contractId: contractId,

  // Original parameters
  originalStake: Number(originalStake),
  tradeSymbol: tradeSymbol,
  duration: Number(duration),
  position: position,

  // Trade execution data
  buyPrice: Number(tradeData.buyPrice),
  payout: Number(tradeData.payout),
  balanceAfter: Number(tradeData.balanceAfter),

  // Timing information
  purchaseTime: purchaseTime * 1000, // Convert to milliseconds
  expectedExpiryTime: expectedExpiryTime,
  waitTime: waitTime,
  waitTimeSeconds: Math.ceil(waitTime / 1000), // For the Wait node

  // Execution metadata
  executionTimestamp: new Date().toISOString(),
  transactionId: tradeData.transactionId,
  longcode: tradeData.longcode,
  shortcode: tradeData.shortcode,

  // Status
  status: 'executed',
  message: 'Trade executed successfully - monitoring for completion',

  // Calculate initial commission
  actualBrokerCommission: Number(originalStake - tradeData.buyPrice),

  // Additional data for processing
  profitPercentage: inputData.profitPercentage || 0,

  // Retry tracking
  retryCount: 0,
  maxRetries: 2
};

// Process completed trade result
const statusResponse = $json;
const originalTradeData = $input.all()[0].json;

if (!statusResponse || !statusResponse.success || !statusResponse.data) {
  throw new Error("Invalid contract status response");
}

const contract = statusResponse.data;
const contractId = originalTradeData.contractId;

console.log(`‚úÖ Trade ${contractId} completed with status: ${contract.status}`);
console.log(`Profit: ${contract.profit}, Payout: ${contract.payout}`);

// Extract real results
const profit = contract.profit || 0;
const payout = contract.payout || 0;
const buyPrice = contract.buyPrice || originalTradeData.buyPrice;
const tradeWon = profit > 0;

// Calculate accurate values
const actualPayout = tradeWon ? payout : 0;
const actualLoss = tradeWon ? 0 : originalTradeData.originalStake;
const netProfit = tradeWon ? (actualPayout - originalTradeData.originalStake) : -originalTradeData.originalStake;
const actualBrokerCommission = originalTradeData.originalStake - buyPrice;

return {
  // Trade identification
  tradeId: originalTradeData.tradeId,
  contractId: contractId,

  // Original parameters
  originalStake: originalTradeData.originalStake,
  tradeSymbol: originalTradeData.tradeSymbol,
  duration: originalTradeData.duration,
  position: originalTradeData.position,

  // Trade execution
  buyPrice: Number(buyPrice),

  // Trade results
  tradeWon: tradeWon,
  actualPayout: Number(actualPayout),
  actualLoss: Number(actualLoss),
  profit: Number(profit),
  netProfit: Number(netProfit),
  actualBrokerCommission: Number(actualBrokerCommission),

  // Metadata
  tradeResult: tradeWon ? "WIN" : "LOSS",
  finalTimestamp: new Date().toISOString(),

  // Contract details
  entrySpot: contract.entrySpot,
  exitSpot: contract.exitSpot,
  currentSpot: contract.currentSpot,
  longcode: contract.longcode || originalTradeData.longcode,
  shortcode: contract.shortcode || originalTradeData.shortcode,

  // Additional data for notifications
  profitPercentage: originalTradeData.profitPercentage || 0,

  // Completion metadata
  completionMethod: 'polling',
  timestamp: originalTradeData.executionTimestamp
};

